import{_ as p,c as e,o,d as t}from"./app.b0f20e76.js";const u='{"title":"\u300A\u5199\u4EE3\u7801\u5361\u4F4F\u600E\u4E48\u529E\u300B","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u300A\u5199\u4EE3\u7801\u5361\u4F4F\u600E\u4E48\u529E\u300B","slug":"\u300A\u5199\u4EE3\u7801\u5361\u4F4F\u600E\u4E48\u529E\u300B"}],"relativePath":"books/blog2.md"}',a={},n=t('<h2 id="\u300A\u5199\u4EE3\u7801\u5361\u4F4F\u600E\u4E48\u529E\u300B" tabindex="-1">\u300A\u5199\u4EE3\u7801\u5361\u4F4F\u600E\u4E48\u529E\u300B <a class="header-anchor" href="#\u300A\u5199\u4EE3\u7801\u5361\u4F4F\u600E\u4E48\u529E\u300B" aria-hidden="true">#</a></h2><p>\u5728\u6211\u4F7F\u7528cocos\u8FDB\u884C\u7F16\u7A0B\u7684\u65F6\u5019 \u6211\u6709\u601D\u8DEF\uFF0C\u4F46\u662F\u4E00\u5F00\u59CB\u5C31\u5361\u4F4F\u4E0D\u77E5\u8BE5\u5982\u4F55\u5F00\u59CB</p><p>\u6BD4\u5982\u5199\u4E00\u4E2A\u7B80\u5355\u7684\u76D1\u542C\uFF0C\u6211\u5C31\u4E0D\u8BB0\u5F97\u600E\u4E48\u5199\u7684\u4E86</p><p>\u518D\u5C31\u662F\u5BF9\u4E8E\u7F16\u7A0B\u5BF9\u8C61\u7684\u7406\u89E3\uFF0C\u6211\u8981\u6539\u7684\u5230\u5E95\u662F\u4E2A\u4EC0\u4E48\u73A9\u610F\uFF0C\u6211\u7684\u4EE3\u7801\u903B\u8F91\u7684\u4F5C\u7528\u5BF9\u8C61\u5230\u5E95\u662F\u8C01\uFF1F\uFF0C\u5728cocos\u91CC\u9762\uFF0C\u5F15\u64CE\u7684\u57FA\u7840\u5BF9\u8C61\uFF0C\u5C31\u662Fnode\uFF0C\u8FD8\u6709node\u7684\u7EC4\u4EF6\uFF0Cnode\u4E0B\u9762\u7684\u5B50node\uFF0Cnode\u4E0A\u9762\u7684\u7236node\u3002</p><p>\u4FEE\u6539node\u7684scale\uFF0Cposition\u8FD9\u4E9B\uFF0C\u4E5F\u53EF\u4EE5\u4FEE\u6539Rigibody2D\uFF0C\u672C\u8D28\u4E0A\u4E5F\u53EA\u662F\u8BA9\u7EC4\u4EF6\u5E2E\u52A9\u6211\u5B8C\u6210\u4E00\u4E9B\u5DE5\u4F5C\u3002</p><p>\u7136\u540E\u4E5F\u662F\u4E00\u4E9B\u903B\u8F91\u4E0A\u7684\u4FEE\u6539\uFF0C\u5904\u7406\u4EC0\u4E48\u5462\uFF0C\u53BB\u62FF\u5F39\u7A97\u7684\u5F15\u7528\uFF0C\u6BD4\u5982\u8BAD\u7EC3\u754C\u9762\u7684\u4E00\u4E2A\u7EB8\u7247\u4EBA\uFF0C\u70B9\u51FB\u4E4B\u540E\uFF0C\u5C31\u8981\u53BB\u62FF\u5230\u5F39\u7A97\u7684\u8C03\u7528\u811A\u672C\uFF0C\u628A\u81EA\u5DF1\u662F\u8C01\u4F20\u5165\u8FDB\u53BB\uFF0C\u7136\u540E\u8C03\u7528\u5F39\u7A97\u7684\u65B9\u6CD5\u3002</p><p>\u5F39\u7A97\u7684\u4EC0\u4E48\u65B9\u6CD5\uFF1F\u4E5F\u5C31\u662Factive\u7684false\u548Ctrue\uFF0C\u7528\u8FD9\u4E2A\u6765\u63A7\u5236\u663E\u793A\uFF0C\u4E5F\u53EF\u4EE5\u4F7F\u7528\u5C42\u7EA7\uFF0C\u8C03\u5230\u4E0B\u9762\u770B\u4E0D\u89C1\u3002 \u5F39\u7A97\u91CC\u9762\u53C8\u6709\u4EC0\u4E48\uFF0C\u4E00\u4E2A\u662F\u5B83\u81EA\u5DF1\u672C\u6765\u8981\u663E\u793A\u7684\u4E1C\u897F\u3002 \u5B83\u53C8\u8981\u548C\u5916\u90E8\u8FDB\u884C\u4EA4\u4E92\uFF0C\u6BD4\u5982\u5C31\u8981\u62FF\u5230\u6570\u636E\u7CFB\u7EDF\u7684\u64CD\u63A7\u6743\uFF0C\u80FD\u591F\u6839\u636E\u73A9\u5BB6\u884C\u4E3A\uFF0C\u53BB\u5BF9\u6570\u636E\u7CFB\u7EDF\u8FDB\u884C\u63A5\u53E3\u7684\u8C03\u7528\uFF0C\u589E\u52A0\u7ECF\u9A8C\u503C\uFF0C\u7B49\u7EA7\uFF0C\u6362\u88C5\u5907</p><p>\u6362\u88C5\u5907\u53C8\u8981\u5F39\u7A97\uFF0C\u67E5\u8BE2\u5230\u88C5\u5907\uFF0C\u70B9\u51FB\u4E0A\u53BB\uFF0C\u7136\u540E\u53C8\u8981\u8FD4\u56DE\u7ED9\u8FD9\u4E2A\u5F39\u7A97\u7CFB\u7EDF\uFF0C\u9EBB\u70E6\u5F97\u5F88\u3002\u8FD8\u5F97\u6162\u6162\u505A\u3002</p><p>\u7CFB\u7EDF\u4E0D\u662F\u72EC\u7ACB\u7684\u7CFB\u7EDF\uFF0C\u6BCF\u4E2A\u7CFB\u7EDF\u90FD\u6709\u5B83\u81EA\u5DF1\u9700\u8981\u7684\u5916\u90E8\u8054\u7CFB\uFF0C\u505A\u4E00\u4E2A\u7CFB\u7EDF\uFF0C\u4EC5\u4EC5\u53EA\u662F\u628A\u5B83\u7684UI\u8868\u73B0\u505A\u51FA\u6765\u5417\uFF0C\u4E0D\u591F\uFF0C\u8FD8\u6709\u5B83\u7684UI\u6240\u9700\u8981\u7684\u5916\u90E8\u7CFB\u7EDF\u7684\u63A5\u53E3\uFF0C\u8FD9\u4E2A\u662F\u5F88\u91CD\u8981\u7684\u3002 \u4E0D\u80FD\u5206\u5F97\u592A\u6563\uFF0C\u4E5F\u4E0D\u80FD\u5F04\u6210\u4E00\u5768\u3002\u603B\u8981\u770B\u7684\u6E05\u695A\uFF0C\u5206\u7684\u660E\u767D\uFF0C\u8FD8\u80FD\u5199\u5F97\u4E0B\u53BB\uFF0C\u4E0D\u7136\u9879\u76EE\u5C31\u6CA1\u6CD5\u505A\u4E0B\u53BB\u4E86\u3002</p><p>\u6240\u4EE5\u4E0D\u540C\u7684\u7CFB\u7EDF\u4E4B\u95F4\u5982\u4F55\u8FDB\u884C\u901A\u4FE1\u5462\uFF0C\u9009\u7528\u5168\u5C40\u5355\u4F8B\u8FDB\u884C\u4FE1\u606F\u4F20\u9012\uFF0C\u8FD9\u4E2A\u53EF\u4EE5\uFF0C\u6CE8\u610F\u4FE1\u606F\u7684\u89C4\u8303\u6027\u3002</p><p>\u8FD8\u6709\u7528\u5230\u7684\u5C5E\u6027\u68C0\u67E5\u5668\uFF0C\u628A\u4E00\u4E2A\u6309\u94AE\u62D6\u8FDB\u53BB\uFF0C\u5C31\u80FD\u7528\u3002 \u8FD8\u6709\u5C31\u662F\u4EE3\u7801\u91CC\u9762\u7684\u5F15\u7528\uFF0C\u628A\u8FD9\u4E2A\u4EE3\u7801\u5BFC\u5165\u8FDB\u53BB\uFF0C\u5982\u679C\u7CFB\u7EDF\u4E0D\u662F\u5355\u4F8B\u5C31\u4E0D\u80FD\u7528\uFF0C\u5982\u679C\u7CFB\u7EDF\u4E0D\u662F\u9644\u5C5E\u5173\u7CFB\u4E5F\u4E0D\u80FD\u7528\u3002</p><p>\u539F\u6765\u5982\u6B64\uFF0C\u6211\u4E4B\u524D\u4E00\u76F4\u5728\u505A\u5355\u72EC\u7684\u7CFB\u7EDF\uFF0Cmain\u573A\u666F\u7684\uFF0Ctrain\u573A\u666F\u7684\u662F\u5206\u5F00\u505A\u7684\uFF0C\u6240\u4EE5\u7CFB\u7EDF\u4E4B\u95F4\u7684\u534F\u4F5C\u5173\u7CFB\u8FD8\u6CA1\u4F53\u73B0\u51FA\u6765\u3002 \u4F46\u662F\u5F53\u591A\u7CFB\u7EDF\u4E4B\u95F4\u9700\u8981\u534F\u4F5C\u7684\u65F6\u5019\uFF0C\u8FD9\u79CD\u56F0\u96BE\u5C31\u8868\u73B0\u51FA\u6765\u4E86\u3002 \u6211\u4F7F\u7528\u7684\u662F\u6570\u636E\u9A71\u52A8\u7ED3\u6784\uFF0Ca\u7CFB\u7EDF\u628A\u6570\u636E\u6539\u4E86\uFF0Cb\u7CFB\u7EDF\u8BFB\u53D6\u7684\u65F6\u5019\u81EA\u7136\u5C31\u77E5\u9053\uFF0C\u4ED6\u4EEC\u4E24\u4E2A\u4E4B\u95F4\u5E76\u6CA1\u6709\u4EC0\u4E48\u76F4\u63A5\u7684\u901A\u4FE1\u534F\u4F5C\u5173\u7CFB\u3002</p><p>\u4F46\u662F\uFF0C\u4EBA\u7269\u57F9\u517B\u754C\u9762\uFF0C\u4ED6\u5C31\u6709\u5B83\u81EA\u5DF1\u7684\u5B50\u5F39\u7A97\uFF0C\u751A\u81F3\u5B50\u5F39\u7A97\u8FD8\u6709\u5B50\u5F39\u7A97\uFF0C\u5F39\u7A97\u7684\u5C42\u7EA7\u5F88\u591A\uFF0C\u5C31\u9700\u8981\u4E00\u4E2A\u4E13\u95E8\u7684\u7BA1\u7406\u7CFB\u7EDF\u3002</p><p>\u4E0D\u540C\u7684\u573A\u666F\u91CC\u9762\uFF0C\u8BE5\u600E\u4E48\u6784\u9020\u51FA\u4E0D\u540C\u7684\u6E38\u620F\u7CFB\u7EDF\uFF1F \u4E0D\u540C\u7684\u573A\u666F\u91CC\u9762\uFF0C\u6240\u9700\u8981\u7684\u7CFB\u7EDF\u4E5F\u662F\u4E0D\u4E00\u6837\u7684\u3002</p><p>\u4F7F\u7528\u7A7A\u8282\u70B9+\u903B\u8F91\u4EE3\u7801--\u6DFB\u52A0\u63A7\u5236\u8282\u70B9\u7684\u65B9\u5F0F\u6784\u5EFA\u573A\u666F\u3002</p><p>\u573A\u666F\u91CC\u9762\u5230\u5E95\u9700\u8981\u4E9B\u4EC0\u4E48\u4E1C\u897F\u3002\u591A\u4E2A\u903B\u8F91\u7CFB\u7EDF\uFF0C\u8FD9\u4E2A\u6BCF\u4E2A\u573A\u666F\u7684\u5927\u8111\u3002 \u591A\u4E2A\u529F\u80FD\u8282\u70B9\uFF0Clabel\uFF0Csprite\uFF0C\u8FD9\u662F\u6BCF\u4E2A\u573A\u666F\u9700\u8981\u64CD\u63A7\u7684\u4E1C\u897F</p><p>\u8FD8\u6709\u4E00\u90E8\u5206\u7684\u9AD8\u7EA7\u8282\u70B9\uFF0C\u81EA\u5E26\u903B\u8F91\u7684\u8282\u70B9\u3002</p><p>\u6E38\u620F\u7F16\u7A0B\uFF0C\u62BD\u8C61\u70B9\u8BF4\u5C31\u662F\u4EC0\u4E48\uFF1F\u8282\u70B9\u63A7\u5236\u8282\u70B9\u3002 \u6700\u7B80\u5355\u7684\uFF0C\u6CA1\u6709\u903B\u8F91\u4E0D\u7528\u88AB\u63A7\u5236\u7684\u8282\u70B9\uFF0C\u573A\u666F\u7684\u80CC\u666F\uFF0C\u53EA\u53D7\u5230\u573A\u666F\u5207\u6362\u7684\u5F71\u54CD \u53EA\u6709\u81EA\u5DF1\u72EC\u7ACB\u903B\u8F91\u7684\u8282\u70B9\uFF0C\u4E00\u9897\u5B50\u5F39\uFF0C\u5C04\u51FA\u53BB\uFF0C\u7531\u7269\u7406\u7CFB\u7EDF\u63A5\u7BA1\uFF0C\u53D1\u751F\u4EC0\u4E48\u78B0\u649E\u903B\u8F91\uFF0C\u8BBE\u8BA1\u8005\u90FD\u4E0D\u7528\u7BA1\u3002 \u518D\u590D\u6742\u70B9\uFF0C\u4F5C\u8005\u81EA\u5DF1\u8BBE\u8BA1\u7684\u903B\u8F91\u8282\u70B9\u3002 \u4E00\u4E2A\u7EB8\u7247\u4EBA\uFF0C\u62E5\u6709\u81EA\u5DF1\u7684\u653B\u51FB\uFF0C\u7279\u6548\uFF0C\u53D7\u51FB\u903B\u8F91\uFF0C\u8FD9\u4E2A\u548C\u4E0D\u662F\u5355\u7EAF\u7269\u7406\u7CFB\u7EDF\uFF0C\u6240\u4EE5\u8FD8\u8981\u7ED9\u4ED6\u4EEC\u8BBE\u7F6E\u4E00\u4E2A\u7CFB\u7EDF\uFF0C\u6765\u7BA1\u7406\u8FD9\u4E9B\u62E5\u6709\u81EA\u5DF1\u903B\u8F91\u7684\u8282\u70B9\u3002 \u4E00\u4E2A\u6218\u6597\u7CFB\u7EDF\uFF0C\u4F7F\u7528\u7EB8\u7247\u4EBA\u9884\u5236\u4F53\uFF0C\u5B58\u50A8\u4ED6\u4EEC\u7684\u5F15\u7528\uFF0C\u7136\u540E\u6765\u5BF9\u4ED6\u4EEC\u8FDB\u884C\u63A7\u5236\u3002</p><p>\u7CFB\u7EDF\u4E0D\u80FD\u6CA1\u6709\u8FB9\u754C\uFF0C\u4E0D\u7136\u8FD9\u4E2A\u7CFB\u7EDF\u5C31\u96BE\u4EE5\u4F7F\u7528\uFF0C\u8FD9\u4E2A\u7CFB\u7EDF\u8981\u6709\u660E\u786E\u7684\u8F93\u5165\u63A5\u53E3\uFF0C\u6307\u660E\u8F93\u5165\u4E00\u4E9B\u4EC0\u4E48\uFF0C\u5C31\u80FD\u591F\u63A7\u5236\u5B83\u3002\u4E00\u4E2A\u7CFB\u7EDF\u9700\u8981\u80FD\u591F\u88AB\u5C01\u88C5\uFF0C\u8FD9\u6837\u624D\u80FD\u65E2\u6EE1\u8DB3\u590D\u6742\u7684\u529F\u80FD\u9700\u6C42\uFF0C\u53C8\u80FD\u8FBE\u5230\u7CFB\u7EDF\u95F4\u76F8\u4E92\u5408\u4F5C\u7684\u76EE\u7684\uFF0C\u4E0D\u7136\uFF0C\u4E00\u76D8\u6563\u6C99\uFF0C\u8C01\u4E5F\u4E0D\u77E5\u9053\u8BE5\u5982\u4F55\u53BB\u4F7F\u7528\u4ED6\u4EEC\u3002</p><p>\u7CFB\u7EDF\u5185\u7684\u7EC4\u7EC7\uFF0C\u4E00\u4E2A\u7EB8\u7247\u4EBA\uFF0C\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u7CFB\u7EDF\uFF0C\u5B83\u5177\u6709\u81EA\u5DF1\u7684\u8282\u70B9\uFF0C\u81EA\u5DF1\u7684\u7EC4\u4EF6\uFF0C\u81EA\u5DF1\u7684\u4EE3\u7801\u903B\u8F91\uFF0C\u6E05\u695A\u5730\u5B9A\u4E49\u4E86\u5BF9\u5916\u7684\u63A5\u53E3\u3002\u628A\u5B83\u505A\u6210\u4E86\u4E00\u4E2A\u9884\u5236\u4F53\u3002</p><p>\u6218\u6597\u7CFB\u7EDF\uFF0C\u628A\u4E00\u4E2A\u7CFB\u7EDF\u5206\u6210\u51E0\u90E8\u5206\uFF0C\u7B2C\u4E00\u90E8\u5206\u7528\u6765\u505A\u9884\u5904\u7406\uFF0C\u5F15\u7528\u6C60\u7BA1\u7406\uFF0C\u63D0\u53D6\u591A\u4E2A\u9884\u5236\u4F53\uFF0C\u63A5\u53D7\u8F93\u5165\u7684\u6570\u636E\uFF0C\u7B2C\u4E8C\u90E8\u5206\u751F\u6210\u6218\u6597\u4E5D\u5BAB\u683C\uFF0C\u4E5D\u5BAB\u683C\u4E5F\u662F\u8282\u70B9\uFF0C\u53EA\u662F\u6CA1\u6709\u81EA\u5DF1\u7684\u903B\u8F91\u3002\uFF0C\u7B2C\u4E09\u90E8\u5206\u5C31\u7EFC\u5408\u524D\u4E24\u4E2A\u90E8\u5206\uFF0C\u628A\u4EBA\u7269\u5206\u6D3E\u5230\u5B83\u81EA\u5DF1\u7684\u683C\u5B50\u4E0A\uFF0C\u5B58\u50A8\u5B83\u7684\u5F15\u7528\u3002 \u8FD9\u6837\u4E00\u770B\u8D77\u6765\uFF0C\u4E00\u5207\u5C31\u5B8C\u5907\u4E86\u3002 \u4EBA\u7269\u7684\u6218\u6597\uFF0C\u4EBA\u7269\u7684\u6218\u6597\u9700\u8981\u77E5\u9053\u6211\u65B9\u7684\u6570\u636E\uFF0C\u5BF9\u65B9\u7684\u6570\u636E\uFF0C\u8FD8\u8981\u77E5\u9053\u5BF9\u65B9\u662F\u8C01\uFF1F \u6240\u4EE5\u8FD9\u4E2A\u6218\u6597\u7CFB\u7EDF\u53C8\u9700\u8981\u4E00\u4E2A\u7BA1\u7406\u7CFB\u7EDF\uFF0C\u80FD\u591F\u7ED9\u89D2\u8272\u63D0\u4F9B\u67E5\u8BE2\uFF0C\u544A\u8BC9\u4ED6\u4F60\u8BE5\u653B\u51FB\u8C01\uFF0C\u544A\u8BC9\u88AB\u653B\u51FB\u7684\uFF0C\u4F60\u88AB\u653B\u51FB\u4E86\u3002 \u8FD9\u4E2A\u7CFB\u7EDF\u505A\u8D77\u6765\u4E0D\u5BB9\u6613\uFF0C\u4E3A\u4EC0\u4E48\uFF1F \u56E0\u4E3A\u4E00\u5F00\u59CB\uFF0C\u8C01\u4E5F\u4E0D\u77E5\u9053\u5230\u5E95\u8FD8\u9700\u8981\u90A3\u4E9B\u7CFB\u7EDF\uFF0C\u4EBA\u7269\u8FD8\u6CA1\u6709\u5B9A\u578B\uFF0C\u8FD9\u4E2A\u8BE5\u600E\u4E48\u505A\uFF0C\u90A3\u4E2A\u6539\u600E\u4E48\u505A\u3002 \u8FD9\u6837\u505A\u8D77\u6765\u50CF\u65E0\u5934\u82CD\u8747\uFF0C\u6EE1\u5C4B\u5B50\u4E71\u98DE\uFF0C\u505A\u5230\u54EA\u91CC\u7B97\u54EA\u91CC\uFF0C\u8FD9\u6837\u592A\u6162\uFF0C\u592A\u6CA1\u6548\u7387\u3002 \u9700\u8981\u7528\u5DE5\u7A0B\u5316\u7684\u65B9\u6CD5\u3002\u600E\u4E48\u505A\uFF1F\u5148\u5B9A\u597D\u5927\u81F4\u89C4\u8303\uFF0C\u628A\u5927\u6982\u7684\u7CFB\u7EDF\u5212\u5206\u51FA\u6765\uFF0C\u7136\u540E\u518D\u53BB\u586B\u8865\uFF0C\u4E0D\u7136\uFF0C\u6309\u7167\u8FD9\u79CD\u4E0D\u65AD\u649E\u5899\u5F00\u8DEF\u7684\u65B9\u5F0F\uFF0C\u5929\u77E5\u9053\u5230\u5E95\u8981\u91CD\u6784\u591A\u5C11\u6B21\uFF1F\u5199\u4EE3\u7801\u5C11\u4E86\uFF0C\u5927\u591A\u6570\u65F6\u95F4\u90FD\u7528\u5230\u60F3\u4EE3\u7801\u53BB\u4E86\u3002\u5982\u679C\u4E00\u5F00\u59CB\u5C31\u80FD\u5212\u5206\u597D\u7684\u8BDD\uFF0C\u505A\u8D77\u6765\u5C31\u5BB9\u6613\u591A\u4E86\u3002</p><p>\u6240\u4EE5\u8BE5\u600E\u4E48\u505A\u5462\uFF1F\u5728\u4E00\u5F00\u59CB\uFF0C\u5C31\u5E94\u8BE5\u8981\u5BF9\u591A\u4E2A\u7CFB\u7EDF\u8FDB\u884C\u4E00\u4E2A\u68B3\u7406\uFF0C\u5927\u6982\u660E\u767D\uFF0C\u5230\u5E95\u9700\u8981\u4E9B\u4EC0\u4E48\u7CFB\u7EDF\uFF0C\u8FD9\u4E2A\u53EB\u505A\uFF0C\u5DE5\u7A0B\u8BA1\u5212\u4E66\u3002</p><p>\u5DE5\u7A0B\u8BA1\u5212\u4E66\u4E5F\u4E0D\u662F\u5B8C\u5168\u56FA\u5B9A\u7684\uFF0C\u7EDD\u5BF9\u4E5F\u4F1A\u6709\u4E0D\u5408\u7406\u7684\u5730\u65B9\uFF0C\u4E5F\u4F1A\u9700\u8981\u91CD\u6784\uFF0C\u91CD\u5199\uFF0C\u4F46\u662F\u8FD9\u6BD4\u8D77\u4E00\u5F00\u59CB\u5C31\u5B8C\u5168\u6CA1\u6709\u8FD8\u662F\u8981\u597D\u592A\u591A\u4E86\u3002 \u597D\uFF0C\u60F3\u5230\u4E86\u529E\u6CD5\uFF0C\u90A3\u5C31\u5F00\u59CB\u5B9E\u884C\u8BD5\u8BD5\u770B\u5427\u3002</p>',23),s=[n];function r(_,c,d,i,l,h){return o(),e("div",null,s)}var b=p(a,[["render",r]]);export{u as __pageData,b as default};
